const validUEI=d=>{if(d.length!=12){return false}if(d[0]=="0"){return false}if(d.toUpperCase().includes("O")||d.toUpperCase().includes("I")){return false}if(d.match(/\d{9}/)){return false}if(!checkDigit(d)){return false}return true};const checkDigit=d=>{const reducerStep=m=>{return m.map((i,j)=>{return i*(j+1)%10}).reduce((i,j)=>{return i+j},0)};let s0=d.substr(0,d.length-1).split("").map(i=>i.charCodeAt(0));let res=reducerStep(s0);while(res>9){res=reducerStep(res.toString().split("").map(i=>parseInt(i)))}return res==d[d.length-1]};export{validUEI};
